import { Component, EventEmitter,Output } from '@angular/core';
import { NgModule } from '@angular/core';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent {

searchInputValue: any = "";

// search input value event
@Output()
searchInputValueEvent: EventEmitter<any> = new EventEmitter<any>();

onSearchValueChange(){
  this.searchInputValueEvent.emit(this.searchInputValue);
}

}
<div>
    <div class="searchDiv">
        <svg focusable="false" viewBox="0 0 24 24">
            <path
                d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z">
            </path>
        </svg>

        <input type="text" placeholder="Search" (input)="onSearchValueChange()" [(ngModel)]="searchInputValue"
            maxlength="10">

    </div><br><br>
    <span *ngIf=" searchInputValue != ''; else check ">Searching results of : <span class="resultSpan">" {{
            searchInputValue }} "</span></span>
    <ng-template #check>
        <span>No value entered to search</span>
    </ng-template>
</div>
