        street: new FormControl(null, [Validators.required, Validators.pattern(/^[A-Za-z]$/)]),
        buildingNumber: new FormControl(null, [Validators.pattern("^[0-9].*"), Validators.required]),
        gender: new FormControl(null, Validators.required)
      })
    });
  }
  //* Submitting the form
  onRegFormSubmit() {
    if (this.registrationFormGroup.valid) {
      this.userInscriptionDetails = this.combineFaces();
      this.http.post(`${baseURL}inscription.php`, this.userInscriptionDetails).subscribe(response => {
        console.log(this.userInscriptionDetails);
        // Handle success response
        if (response[0] === "An account associated to this email!") {
          Swal.fire('Ops', response[0], 'error');
        } else if (response[0] === "Account has been successfully registered") {
          Swal.fire('Good job!', response[0], 'success');
        } else {
          Swal.fire({
            title: 'Error!',
            text: response[0],
            icon: 'error',
            confirmButtonText: 'Try Again'
          });
        }
      }, error => {
        // Handle error response
        Swal.fire({
          title: 'Error!',
          text: error[0],
          icon: 'error',
          confirmButtonText: 'Cool'
        });
      });
    } else {
      //* Refresh the page , Invalid inputs
      Swal.fire({
        title: 'Error!',
        text: "Invalid Inputs, refresh the page and try again!",
        icon: 'error',
        confirmButtonText: "Refresh"
      }).then(() => {
        this.refresh();
      });
    }
  }
  //* Refresh the page 
  refresh() {
    this.router.navigateByUrl('/', {
      skipLocationChange: true
    }).then(() => {
      this.router.navigate([this.router.navigate(['/login'])]);
    });
  }
  //* Combine the values of the three faces into one object
  combineFaces() {
    // using this.firstName to define the new object give an error 
