p[_ngcontent-%COMP%] {\n  position: absolute;\n  width: 100%;\n  padding: 0 35px;\n  top: 50%;\n  left: 0;\n  transform: translate3d(0, -50%, 35px) perspective(100px);\n  z-index: 20;\n  display: block;\n}\n\n.form-group[_ngcontent-%COMP%], .form-floating[_ngcontent-%COMP%] {\n  position: relative;\n  display: block;\n  margin: 0;\n  padding: 0;\n}\n\n.form-style[_ngcontent-%COMP%], .form-control[_ngcontent-%COMP%] {\n  padding: 13px 20px;\n  padding-left: 55px;\n  height: 48px;\n  width: 100%;\n  font-weight: 500;\n  border-radius: 4px;\n  font-size: 14px;\n  line-height: 22px;\n  letter-spacing: 0.5px;\n  outline: none;\n  color: #c4c3ca;\n  background-color: #1f2029;\n  border: none;\n  transition: all 200ms linear;\n  box-shadow: 0 4px 8px 0 rgba(21, 21, 21, 0.2);\n}\n\n.form-floating[_ngcontent-%COMP%]    > label[_ngcontent-%COMP%] {\n  left: 13%;\n}\n\n.form-style[_ngcontent-%COMP%]:focus, .form-style[_ngcontent-%COMP%]:active {\n  border: none;\n  outline: none;\n  box-shadow: 0 4px 8px 0 rgba(21, 21, 21, 0.2);\n}\n\n.input-icon[_ngcontent-%COMP%] {\n  position: absolute;\n  top: 0;\n  left: 18px;\n  height: 48px;\n  font-size: 24px;\n  line-height: 48px;\n  text-align: left;\n  color: #088178;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:-ms-input-placeholder {\n  color: #c4c3ca;\n  opacity: 0.7;\n  -ms-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]::-moz-placeholder {\n  color: #c4c3ca;\n  opacity: 0.7;\n  -moz-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:-moz-placeholder {\n  color: #c4c3ca;\n  opacity: 0.7;\n  -moz-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]::-webkit-input-placeholder {\n  color: #c4c3ca;\n  opacity: 0.7;\n  -webkit-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:focus:-ms-input-placeholder {\n  opacity: 0;\n  -ms-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:focus::-moz-placeholder {\n  opacity: 0;\n  -moz-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:focus:-moz-placeholder {\n  opacity: 0;\n  -moz-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%]:focus::-webkit-input-placeholder {\n  opacity: 0;\n  -webkit-transition: all 200ms linear;\n  transition: all 200ms linear;\n}\n\n.form-group[_ngcontent-%COMP%]   input[name=buildingnumber][_ngcontent-%COMP%] {\n  width: 65px;\n}\n\n.form-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\n  font-size: 12px;\n}\n\n.btn[_ngcontent-%COMP%] {\n  border-radius: 4px;\n  font-size: 13px;\n  font-weight: 600;\n  text-transform: uppercase;\n  transition: all 200ms linear;\n  padding: 10px 15px;\n  margin-top: 100px;\n  letter-spacing: 1px;\n  display: inline-flex;\n  -moz-align-items: center;\n  -ms-align-items: center;\n  align-items: center;\n  -moz-justify-content: center;\n  -ms-justify-content: center;\n  justify-content: center;\n  -ms-flex-pack: center;\n  text-align: center;\n  border: none;\n  background-color: #ffeba7;\n  color: #102770;\n  box-shadow: 0 8px 24px 0 rgba(255, 235, 167, 0.2);\n}\n\nbutton[_ngcontent-%COMP%]:disabled {\n  background-color: grey;\n  color: white;\n  font-size: 10px;\n}\n\n.btn[_ngcontent-%COMP%]:active {\n  background-color: #102770;\n  color: #ffeba7;\n  box-shadow: 0 8px 24px 0 rgba(16, 39, 112, 0.2);\n}\n\n.btn[_ngcontent-%COMP%]:hover {\n  background-color: #102770;\n  color: #ffeba7;\n  box-shadow: 0 8px 24px 0 rgba(16, 39, 112, 0.2);\n}\n\ninput[type=date][_ngcontent-%COMP%]::-webkit-calendar-picker-indicator {\n  filter: invert(0.8);\n  cursor: pointer;\n}\n\ninput[type=date][_ngcontent-%COMP%]::-webkit-calendar-picker-indicator:hover {\n  opacity: 0.8;\n}\n\n.addressResults[_ngcontent-%COMP%] {\n  display: block;\n  width: -moz-fit-content;\n  width: fit-content;\n  border-radius: 2px;\n  cursor: pointer;\n  background-color: white;\n  color: black;\n  position: absolute;\n  z-index: 500;\n  max-height: 200px;\n  overflow-y: scroll;\n  top: 145px;\n}\n\ninput[_ngcontent-%COMP%]:disabled {\n  cursor: default;\n  background-color: #ccc !important;\n  color: #757575 !important;\n  outline: 1px solid #b2b2b2;\n}\n\n.suggestions[_ngcontent-%COMP%] {\n  padding: 5px;\n  border: 0.5px solid black;\n}\n\n.suggestions[_ngcontent-%COMP%]:hover {\n  background-color: rgb(204, 204, 204);\n}\n\n.ng-invalid[_ngcontent-%COMP%]:not(div):not(form) {\n  border-left: 5px solid #a94442;\n}\n.ng-invalid[_ngcontent-%COMP%]:not(div):not(form):focus {\n  box-shadow: 0 0 0 0.25rem rgba(220, 53, 69, 0.25);\n}\n\n.ng-valid[_ngcontent-%COMP%]:not(div):not(form) {\n  border-left: 5px solid #42a948;\n}\n.ng-valid[_ngcontent-%COMP%]:not(div):not(form):focus {\n  box-shadow: 0 0 0 0.25rem rgba(66, 169, 72, 0.25);\n}\n\n.alert[_ngcontent-%COMP%] {\n  padding: 3px;\n  margin-bottom: 5px;\n  border: 1px solid transparent;\n  border-radius: 4px;\n  display: block;\n}\n\n.alert-danger[_ngcontent-%COMP%] {\n  color: #a94442;\n  background-color: #f2dede;\n  border-color: #ebccd1;\n}\n\n.nextArrow[_ngcontent-%COMP%], .previousArrow[_ngcontent-%COMP%] {\n  background-color: #088178;\n  border-radius: 4px;\n  color: #fff;\n  width: 25px;\n  height: 40px;\n  cursor: pointer;\n  float: right;\n  margin-top: 30px;\n  box-shadow: 0 8px 24px 0 rgba(8, 129, 120, 0.3);\n}\n\nsvg[_ngcontent-%COMP%] {\n  transition: fill 2s ease-in;\n}\n\n.secondFace[_ngcontent-%COMP%]   svg[_ngcontent-%COMP%] {\n  margin-left: 10px;\n}\n\n.thirdFace[_ngcontent-%COMP%]   svg[_ngcontent-%COMP%] {\n  margin-top: 100px;\n}\n/*# sourceMappi